DROP TABLE IF EXISTS `posts`;
CREATE TABLE IF NOT EXISTS `posts` (`id` int(11) NOT NULL AUTO_INCREMENT, `title` text NOT NULL, `body` text NOT NULL, `visibility` tinyint NOT NULL DEFAULT 1, PRIMARY KEY (`id`)) ENGINE=InnoDB;
DROP TABLE IF EXISTS `users`;
CREATE TABLE IF NOT EXISTS `users` (`id` int(11) NOT NULL AUTO_INCREMENT, `username` varchar(256) NOT NULL, `password` varchar(256) NOT NULL, PRIMARY KEY (`id`), UNIQUE KEY `username` (`username`)) ENGINE=InnoDB;
DROP TABLE IF EXISTS `comments`;
CREATE TABLE IF NOT EXISTS `comments` (`comment_id` int(11) NOT NULL AUTO_INCREMENT, `name` text NOT NULL, `email` text NOT NULL, `website` text, `body` text NOT NULL, `updated` datetime NOT NULL, `created` datetime NOT NULL, `post_id` int(11) NOT NULL, FOREIGN KEY(post_id) REFERENCES posts(id) ON UPDATE CASCADE ON DELETE RESTRICT, PRIMARY KEY(`comment_id`)) ENGINE=InnoDB;
DROP TABLE IF EXISTS `uploads`;
CREATE TABLE IF NOT EXISTS `uploads` (`upload_id` int(11) NOT NULL AUTO_INCREMENT, `filename` text NOT NULL, `size` int NOT NULL, `type` varchar(256) NOT NULL, `thumbnail` tinyint NOT NULL DEFAULT 1, `created` datetime NOT NULL, PRIMARY KEY(`upload_id`)) ENGINE=InnoDB; "
